        if(this.specialty == value){
            this.specialty = 0;
        }else{
            this.specialty = value;
        }
    },
    slider(){
       let  current = 1
        setInterval(() => {
            if(screen.width < 768){
                if(current == 1){
                    this.$refs.slideTwo.click();
                    current = 2
                }else if(current == 2){
                    this.$refs.slideThree.click();
                    current = 3
                }else if(current == 3){
                    this.$refs.slideOne.click();
                    current = 1
                }
            }
        }, 3000);
    },
  },
  components: {
     NavbarComponent,
    
    
    
  },

};

 
